    private void VerificarBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_VerificarBtnMouseClicked
        //Esta ventana se creo para intentar hacer el UI/UX mas amigable para el usuario.
        //su principal tarea es crear una interfaz grafica que permita al usuario entrar de forma segura al programa
        try {
            for (int i = 0; i == 0; i++) {
                String nombre = NombreTxtField.getText();
                int id = Integer.parseInt(PasswordTxtField.getText());

                if (id == 1155 && nombre.contains("Juan")) {
                    JOptionPane.showMessageDialog(null, "Su ingreso ha sido correcto.");
                    MainMenu ejecuta = new MainMenu();
                    ejecuta.setVisible(true);
                    this.dispose();

                }else{
                    JOptionPane.showMessageDialog(null, "Ingreso erroneo, intente de nuevo.");
                }
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Por favor no utilice simbolos ni letras en la contrase√±a");
        }


    }//GEN-LAST:event_VerificarBtnMouseClicked

